    apiKey: "AIzaSyAH_si4R-8r5DG7P-y-96bRDabaHADFIKQ",
    authDomain: "fir-9b45a.firebaseapp.com",
    projectId: "fir-9b45a",
    storageBucket: "fir-9b45a.appspot.com",
    messagingSenderId: "1003736985748",
    appId: "1:1003736985748:web:e7d0f9bad8960f08334481"
  };
const app = initializeApp(firebaseConfig);
const db = getFirestore(app)
const auth = getAuth(app)

export {
    db,
    auth,
}
